import {useState} from 'react';
const Profile = () => {

    const handleSubmit = (e) => {
        e.preventDefault()
    }


    const [name, setName] = useState('');

    return(
    <div className='wrapper'>
    <form
    onSubmit={handleSubmit}>

    <input type='text'
    value={name}
    onChange={(event) => {setName(event.target.value)}}>

    </input>
    <button>Save</button>
        </form>
        </div>)
}

export default Profile;
