import React from "react";
import NewTweet from "./components/NewTweet";
import TweetsList from "./components/TweetsList";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
    };
  }

  onNewTweet(tweet) {
    this.setState((prevState) => {
      return { tweets: [tweet, ...prevState.tweets] };
    });
  }

  // async componentWillMount() {
  //   const tweets = await JSON.parse(localStorage.getItem("myState"));
  //   if (localStorage.length !== 0) {
  //     this.setState({ tweets });
  //   } else {
  //     this.setState({ tweets: [] });
  //   }
  // }
  // componentWillUpdate(nextProps, nextState) {
  //   localStorage.setItem("myState", JSON.stringify(nextState.tweets));
  // }

  componentDidMount() {
    const tweets = JSON.parse(localStorage.getItem("tweets"));
    if (tweets) {
      this.setState(() => ({ tweets }));
    }
  }
  componentDidUpdate(prevProps, prevState) {
    if (prevState.tweets.length !== this.state.tweets.length) {
      localStorage.setItem("tweets", JSON.stringify(this.state.tweets));
    }
  }

  render() {
    const { tweets } = this.state;
    //console.log(tweets);

    return (
      <div>
        <NewTweet
          onNewTweet={(tweet) => {
            this.onNewTweet(tweet);
          }}
        />
        <TweetsList tweets={tweets} />
      </div>
    );
  }
}

export default App;
