import React from "react";
import NewTweet from "./components/NewTweet";
import TweetsList from "./components/TweetsList";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
    };
  }

  onNewTweet(tweet) {
    this.setState((prevState) => {
      return { tweets: [...prevState.tweets, tweet] };
    });
  }

  componentDidUpdate() {
    localStorage.setItem("myState", JSON.stringify(this.state.tweets));
    this.saveLocal();
  }

  async saveLocal() {
    const tweets = await JSON.parse(localStorage.getItem("myState"));
    console.log(tweets);
    //this.setState({ tweets });
  }

  render() {
    return (
      <div>
        <NewTweet
          onNewTweet={(tweet) => {
            this.onNewTweet(tweet);
          }}
        />
        <TweetsList tweets={this.state.tweets} />
      </div>
    );
  }
}

export default App;
