import React from "react";
import NewTweet from "./components/NewTweet";
import TweetsList from "./components/TweetsList";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
    };
  }
  onNewTweet(newTweet) {
    // if (this.state.tweets) {
    //   localStorage.setItem("myState", JSON.stringify([...this.state.tweets, newTweet])
    //   );
    // }
    this.setState((prevState) => {
      return { tweets: [...prevState.tweets, newTweet] };
    });
  }

  // componentDidMount() {
  //   this.loadTweets();
  // }

  async loadTweets() {
    const localTweets = await JSON.parse(localStorage.getItem("myState"));
    if (localTweets) {
      this.setState({ tweets: localTweets });
    }
  }

  componentWillMount() {
    localStorage.getItem("myState") &&
      this.setState({
        tweets: JSON.parse(localStorage.getItem("myState")),
      });
  }

  componentDidMount() {
    if (!localStorage.getItem("myState")) {
      const tweets = JSON.parse(localStorage.getItem("myState"));
      this.setState({ tweets });
    }
  }
  componentWillUpdate(nextProps, nextState) {
    localStorage.setItem("myState", JSON.stringify(nextState.tweets));
  }

  render() {
    return (
      <div>
        <NewTweet
          onNewTweet={(tweet) => {
            this.onNewTweet(tweet);
          }}
        />
        <TweetsList tweets={this.state.tweets} />
      </div>
    );
  }
}

export default App;
