import React from "react";
import NewTweet from "./components/NewTweet";
import TweetsList from "./components/TweetsList";
import { callTweetList } from "./lib/api";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
    };
  }

  onNewTweet(tweet) {
    this.setState((prevState) => {
      return { tweets: [tweet, ...prevState.tweets] };
    });
  }

  // async componentWillMount() {
  //   const tweets = await JSON.parse(localStorage.getItem("myState"));
  //   if (localStorage.length !== 0) {
  //     this.setState({ tweets });
  //   } else {
  //     this.setState({ tweets: [] });
  //   }
  // }
  // componentWillUpdate(nextProps, nextState) {
  //   localStorage.setItem("myState", JSON.stringify(nextState.tweets));
  // }

  // async callTweetList() {
  //   let url =
  //     "https://micro-blogging-dot-full-stack-course-services.ew.r.appspot.com/tweet";
  //   const response = await fetch(url);
  //   const data = await response.json();
  //   console.log(data);
  //   this.setState(() => ({ tweets: data }));
  // }

  // componentDidMount() {
  //   this.callTweetList()
  //   if (tweets) {
  //   }
  // }
  componentDidMount() {
    const servTweets = callTweetList();
    this.setState(() => ({ tweets: servTweets.tweets }));
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.tweets.length !== this.state.tweets.length) {
      localStorage.setItem("tweets", JSON.stringify(this.state.tweets));
    }
  }

  render() {
    const { tweets } = this.state;
    //console.log(tweets);

    return (
      <div>
        <NewTweet
          onNewTweet={(tweet) => {
            this.onNewTweet(tweet);
          }}
        />
        {console.log(tweets, "in")}
        {/* <TweetsList tweets={tweets} /> */}
      </div>
    );
  }
}

export default App;
