import React from 'core-js/library/fn/reflect/es7/metadata'

class NewTweet extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        content: '',
      };
    }


    render(){
        return(
            <div>
                <textarea
                id='content'
                name='content'
                value={this.state.content}
                //onChange={(event) => useState({content: event.target.value})}
                ></textarea>
            </div>
        )
    }

}

export default NewTweet;
