export const Profile = () => {

    const handleSubmit = (e) => {
        e.preventDefault()
    }


    const [name, setName] = useState('');
    <form
    onSubmit={handleSubmit}>

    <input type='text'
    value={name}
    onChange={(event) => {setName(event.target.value)}}>

    </input>
    <button>Save</button>
        </form>
}
