                onNewTweet={(tweet) => {
                  this.onNewTweet(tweet);
                }}
                isLoading={this.state.isLoading}
              />

              {isLoading && <Loading />}

              <TweetsList tweets={tweets} />
            </Route>
            <Route path="/profile">
              <div>profile</div>
            </Route>
          </Switch>
        </Router> */}
      </div>
    );
  }
}

export default App;
